package com.small.advent2022

import spock.lang.Specification

class TuningTroubleTest extends Specification {
    TuningTrouble tuningTrouble
    void setup() {
        tuningTrouble = new TuningTrouble()
    }

    def "should find start of message"() {
        expect:
        19 == tuningTrouble.locateStartMessage("mjqjpqmgbljsphdztnvjfqwrcgsmlb")
        23 == tuningTrouble.locateStartMessage("bvwbjplbgvbhsrlpgdmjqwftvncz")
        23 == tuningTrouble.locateStartMessage("nppdvjthqldpwncqszvftbrmjlhg")
        29 == tuningTrouble.locateStartMessage("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")
        26 == tuningTrouble.locateStartMessage("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")
        3217 == tuningTrouble.locateStartMessage("")

    }
    def "should find start of packet"() {

        expect:
        7 == tuningTrouble.locateStartPacket("mjqjpqmgbljsphdztnvjfqwrcgsmlb")
        5 == tuningTrouble.locateStartPacket("bvwbjplbgvbhsrlpgdmjqwftvncz")
        6 == tuningTrouble.locateStartPacket("nppdvjthqldpwncqszvftbrmjlhg")
        10 == tuningTrouble.locateStartPacket("nznrnfrfntjfmvfwmzdfjlvtqnbhcprsg")
        11 == tuningTrouble.locateStartPacket("zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw")
        1175 == tuningTrouble.locateStartPacket("")
    }
}
